dt_retorno_w		timestamp;
nr_atendimento_w	bigint;


BEGIN 
IF (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') AND (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')THEN 
	SELECT	MAX(a.nr_atendimento) 
