CREATE OR REPLACE FUNCTION obter_data_aprovacao_compras ( ie_origem_documento_p text, nr_documento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;


BEGIN

if (ie_origem_documento_p = 'SC') then
	begin

	select	dt_autorizacao
	into STRICT	dt_retorno_w
	from	solic_compra
	where	nr_solic_compra = nr_documento_p;

	end;
elsif (ie_origem_documento_p = 'OC') then
	begin

	select	dt_aprovacao
	into STRICT	dt_retorno_w
	from	ordem_compra
	where	nr_ordem_compra = nr_documento_p;

	end;
elsif (ie_origem_documento_p = 'CC') then
	begin

	select	dt_aprovacao
	into STRICT	dt_retorno_w
	from	cot_compra
	where	nr_cot_compra = nr_documento_p;

	end;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
