CREATE OR REPLACE FUNCTION obter_data_aprovacao_montagem ( nr_seq_agenda_p bigint, ie_item_p text) RETURNS varchar AS $body$
DECLARE


ie_aprovacao_w	varchar(1);


BEGIN
if (ie_item_p = 'E') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprovacao_w
	from	agenda_monta_equipamento
	where	nr_seq_agenda 		= nr_seq_agenda_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
elsif (ie_item_p = 'C') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprovacao_w
	from	agenda_monta_cme
	where	nr_seq_agenda 		= nr_seq_agenda_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
elsif (ie_item_p = 'K') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprovacao_w
	from	agenda_pac_pedido
	where	nr_seq_agenda 		= nr_seq_agenda_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
elsif (ie_item_p = 'F') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprovacao_w
	from	agenda_paciente_inf_adic
	where	nr_seq_agenda 		= nr_seq_agenda_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
end if;

return	ie_aprovacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
