CREATE OR REPLACE FUNCTION obter_data_assina_dig_laudo ( nr_seq_assinatura_p bigint, dt_liberacao_p timestamp, dt_seg_aprovacao_p timestamp, dt_aprovacao_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_assinatura_w		timestamp;


BEGIN

if (coalesce(nr_seq_assinatura_p,0) > 0) and
	((dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') or (dt_aprovacao_p IS NOT NULL AND dt_aprovacao_p::text <> '') or (dt_seg_aprovacao_p IS NOT NULL AND dt_seg_aprovacao_p::text <> '')) then
	select	max(dt_assinatura)
	into STRICT	dt_assinatura_w
	from	TASY_ASSINATURA_DIGITAL a
	where	a.nr_sequencia	= nr_seq_assinatura_p;
end if;

return	dt_assinatura_w;

end;
$body$
