CREATE OR REPLACE FUNCTION obter_data_atend_alta (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE




ds_retorno_w		timestamp;




BEGIN

IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN

	SELECT	MAX(a.dt_previsto_alta)
	INTO STRICT	ds_retorno_w
	FROM	atend_previsao_alta a
	WHERE	a.nr_atendimento = nr_atendimento_p
	AND	(a.dt_previsto_alta IS NOT NULL AND a.dt_previsto_alta::text <> '')
        AND IE_SITUACAO ='A';

END IF;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
