CREATE OR REPLACE FUNCTION obter_data_atend_ger ( nr_seq_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_atendimento_w	atendimento_paciente.dt_entrada%type;
dt_retorno_w		atend_paciente_unidade.dt_entrada_unidade%type;


BEGIN


if ( coalesce(nr_seq_atendimento_p, 0) > 0 )then

select	max(dt_entrada)
into STRICT	dt_atendimento_w
from	atendimento_paciente
where	nr_atendimento  = nr_seq_atendimento_p;


if ((dt_atendimento_w IS NOT NULL AND dt_atendimento_w::text <> '')  and dt_atendimento_w > clock_timestamp())then

dt_retorno_w := dt_atendimento_w;

end if;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
