CREATE OR REPLACE FUNCTION obter_data_atend_requisicao ( nr_requisicao_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_atendimento_w	timestamp;

/*É a última data de atendimento da requisicao que pode ser parcial ou a data de baixa*/

BEGIN

begin
select	coalesce(max(a.dt_baixa), max(b.dt_atendimento))
into STRICT	dt_atendimento_w
from	item_requisicao_material b,
	requisicao_material a
where	a.nr_requisicao = b.nr_requisicao
and	a.nr_requisicao = nr_requisicao_p;
exception when others then
	dt_atendimento_w	:= null;
end;

return	dt_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
