CREATE OR REPLACE FUNCTION obter_data_autor_finalizada (nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_hist_autor_w	bigint;
dt_retorno_w		timestamp;


BEGIN
dt_retorno_w	:= null;

select	min(b.nr_sequencia)
into STRICT	nr_seq_hist_autor_w
from	autorizacao_convenio a,
	autorizacao_convenio_hist b,
	estagio_autorizacao c
where	b.nr_sequencia_autor	= a.nr_sequencia
and	b.nr_seq_estagio	= c.nr_sequencia
and	a.nr_sequencia		=  nr_sequencia_p
and	c.ie_interno		= '10';

if (nr_seq_hist_autor_w IS NOT NULL AND nr_seq_hist_autor_w::text <> '') then
	select	max(dt_atualizacao)
	into STRICT	dt_retorno_w
	from	autorizacao_convenio_hist
	where	nr_sequencia	= nr_seq_hist_autor_w;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
