CREATE OR REPLACE FUNCTION obter_data_canc_quimio (nr_seq_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

select	max(dt_cancelada)
into STRICT	dt_retorno_w
from	agenda_quimio a,
		paciente_atendimento b
where	a.nr_seq_atendimento 	= nr_seq_atendimento_p
and		a.nr_seq_atendimento	= b.nr_seq_Atendimento
and		a.dt_Agenda				= coalesce(b.dt_real, b.dt_prevista);

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
