CREATE OR REPLACE FUNCTION obter_data_conversao_conv ( cd_convenio_p bigint, dt_execucao_p timestamp, ie_tipo_item_p text, nr_atendimento_p bigint, dt_conta_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
ie_data_w		varchar(1);

C01 CURSOR FOR
	SELECT	ie_data
	from	conv_regra_data_conversao
	where	ie_tipo = ie_tipo_item_P
	and	dt_execucao_p between dt_vigencia_inicial and coalesce(dt_vigencia_final,dt_execucao_p)
	order by nr_sequencia;


BEGIN
dt_retorno_w:= dt_execucao_p;
begin
open C01;
loop
fetch C01 into
	ie_data_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_data_w = 'A') then
		select	coalesce(max(dt_entrada),dt_execucao_p)
		into STRICT	dt_retorno_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p;
	elsif (ie_data_w = 'C') then
		dt_retorno_w := dt_conta_p;
	else
		dt_retorno_w := dt_execucao_p;
	end if;

	end;
end loop;
close C01;
exception
when others then
dt_retorno_w:= dt_execucao_p;
end;
return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
