CREATE OR REPLACE FUNCTION obter_data_diag_atend (nr_atendimento_p bigint, ie_por_usuario_logado_p bigint DEFAULT 0) RETURNS timestamp AS $body$
DECLARE


dt_diagnostico_w			timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	SELECT	MAX(dt_diagnostico)
	INTO STRICT 	dt_diagnostico_w
	FROM 	diagnostico_medico a
	WHERE	nr_atendimento	= nr_atendimento_p
	AND	EXISTS	(	SELECT	1
				FROM	diagnostico_doenca x
				WHERE	x.nr_atendimento	= a.nr_atendimento
				AND	x.dt_diagnostico	= a.dt_diagnostico
				AND	coalesce(x.dt_inativacao::text, '') = ''
				AND     (	(ie_por_usuario_logado_p = 0) OR (ie_por_usuario_logado_p = 1 AND nm_usuario = Obter_Usuario_Ativo))
					);
end if;

return	dt_diagnostico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
