CREATE OR REPLACE FUNCTION obter_data_edicao_tnm (cd_topografia_p text) RETURNS timestamp AS $body$
DECLARE


dt_edicao_w	timestamp;


BEGIN

if (cd_topografia_p IS NOT NULL AND cd_topografia_p::text <> '') then


	select 	max(DT_EDICAO)
	into STRICT	dt_edicao_w
	from   	cido_topografia a,
		can_tnm_localizacao b
	where  	a.nr_seq_loc_tnm = b.nr_sequencia
	and    	a.cd_topografia = cd_topografia_p;

end if;

return	dt_edicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
