SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_entrada_setor (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_interno_w	bigint;
cd_setor_atendimento_w  bigint;
dt_entrada_unidade_w	timestamp;
dt_entrada_w		timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	max(dt_entrada_unidade)
	into STRICT	dt_entrada_w
	from	atend_paciente_unidade x
	where	x.nr_atendimento = nr_atendimento_p
	and	coalesce(x.dt_saida_unidade::text, '') = '';

	select  max(cd_setor_atendimento)
	into STRICT    cd_setor_atendimento_w
	from	atend_paciente_unidade x
	where	x.nr_atendimento = nr_atendimento_p
	and	dt_entrada_unidade = dt_entrada_w;

	select  coalesce(max(x.nr_seq_interno),0)
	into STRICT    nr_seq_interno_w
	from	atend_paciente_unidade x
	where	x.nr_atendimento = nr_atendimento_p
	and	x.cd_setor_atendimento <> cd_setor_atendimento_w
	and	x.dt_entrada_unidade < dt_entrada_w;


	if (nr_seq_interno_w > 0) then
		select	max(x.dt_entrada_unidade)
		into STRICT	dt_entrada_unidade_w
		from	atend_paciente_unidade x
		where	x.nr_atendimento = nr_atendimento_p
		and	x.cd_setor_atendimento = cd_setor_atendimento_w
		and 	x.nr_seq_interno = ( SELECT  min(d.nr_seq_interno)
					   from    atend_paciente_unidade d
					   where   x.cd_setor_atendimento = d.cd_setor_atendimento
					   and	   d.nr_atendimento = x.nr_atendimento
					   and	d.nr_seq_interno > nr_seq_interno_w);
	end if;


	if (coalesce(dt_entrada_unidade_w::text, '') = '') then
		select	max(x.dt_entrada_unidade)
		into STRICT	dt_entrada_unidade_w
		from	atend_paciente_unidade x
		where	x.nr_atendimento = nr_atendimento_p;
	end if;
end if;

return	dt_entrada_unidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_data_entrada_setor (nr_atendimento_p bigint) FROM PUBLIC;

