SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_entrega_prev_opme ( cd_material_p bigint, nr_seq_protocolo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_entrega_prevista	timestamp;
qt_dias_fornecedor_w	smallint;
dt_retirada_w		timestamp;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN


select  coalesce(max(qt_dia_ressup_forn),0)
into STRICT	 qt_dias_fornecedor_w
from 	 material_estab
where	 cd_estabelecimento = cd_estabelecimento_w
and	 cd_material 	    = cd_material_p;


select   max(dt_retirada)
into STRICT	 dt_retirada_w
from     protocolo_fornec_opme
where    nr_sequencia = nr_seq_protocolo_p;


if (qt_dias_fornecedor_w > 0) and (dt_retirada_w IS NOT NULL AND dt_retirada_w::text <> '') then
	dt_entrega_prevista := dt_retirada_w + qt_dias_fornecedor_w;

end if;

return dt_entrega_prevista;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
