CREATE OR REPLACE FUNCTION obter_data_episodio_plan ( nr_seq_episodio_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_episodio_w	episodio_paciente.dt_episodio%type;
dt_retorno_w	atendimento_paciente.dt_entrada%type;


BEGIN


if ( coalesce(nr_seq_episodio_p, 0) > 0 )then

select	max(dt_episodio)
into STRICT	dt_episodio_w
from	episodio_paciente
where	nr_sequencia  = nr_seq_episodio_p;


if ((dt_episodio_w IS NOT NULL AND dt_episodio_w::text <> '')  and dt_episodio_w > clock_timestamp())then

dt_retorno_w := dt_episodio_w;

end if;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
