CREATE OR REPLACE FUNCTION obter_data_escala_dor_atend (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_escala_dor_w  timestamp;

BEGIN

select 	max(e.dt_sinal_vital)
into STRICT	dt_escala_dor_w
from	atendimento_sinal_vital e
where	(e.cd_escala_dor IS NOT NULL AND e.cd_escala_dor::text <> '')
and		e.ie_situacao = 'A'
and		e.nr_atendimento = nr_atendimento_p
and		coalesce(e.ie_rn,'N')	= 'N'
and		(e.dt_liberacao IS NOT NULL AND e.dt_liberacao::text <> '')
and		coalesce(e.dt_inativacao::text, '') = '';


return dt_escala_dor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_data_escala_dor_atend (nr_atendimento_p bigint) FROM PUBLIC;

