CREATE OR REPLACE FUNCTION obter_data_estab_mat_dil (cd_material_p bigint, dt_atualizacao_nrec_p timestamp) RETURNS timestamp AS $body$
DECLARE


qt_estabilidade_w	bigint;


BEGIN

select 	min(converte_estab_hor(ie_tempo_estab, qt_estabilidade))
into STRICT	qt_estabilidade_w
from 	material_armazenamento
where 	cd_material = cd_material_p
and 	nr_seq_estagio = (SELECT max(nr_sequencia)
                          from mat_estagio_armaz
						  where ie_estagio = 1)
and		coalesce(cd_estabelecimento, coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)) = coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);

if (coalesce(qt_estabilidade_w::text, '') = '') then
	qt_estabilidade_w := 0;
end if;

return	dt_atualizacao_nrec_p + qt_estabilidade_w/24;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
