CREATE OR REPLACE FUNCTION obter_data_estagio_atual (nr_seq_pendencia_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_hist_w		bigint;
dt_retorno_w		timestamp;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_hist_w
from 	cta_pendencia_hist
where 	nr_seq_pend = nr_seq_pendencia_p;

if (nr_seq_hist_w > 0) then

	select 	dt_inicio_estagio
	into STRICT	dt_retorno_w
	from 	cta_pendencia_hist
	where 	nr_sequencia = nr_seq_hist_w;

else

	select 	dt_pendencia
	into STRICT	dt_retorno_w
	from 	cta_pendencia
	where 	nr_sequencia = nr_seq_pendencia_p;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
