CREATE OR REPLACE FUNCTION obter_data_etapa_npi ( nr_seq_etapa bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;


BEGIN
select	coalesce(max(a.qt_dias),0)
into STRICT	qt_retorno_w
from	prp_regra_tempo_etapa a
where	a.nr_seq_etapa_processo = nr_seq_etapa;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
