CREATE OR REPLACE FUNCTION obter_data_evento_adep ( nr_seq_evento_p bigint, ie_tipo_item_p text) RETURNS timestamp AS $body$
DECLARE


dt_evento_w	timestamp;


BEGIN

if (ie_tipo_item_p = 'SOL') then
	select	max(dt_alteracao)
	into STRICT	dt_evento_w
	from	prescr_solucao_evento
	where	nr_sequencia = nr_seq_evento_p;

else
	select	max(dt_alteracao)
	into STRICT	dt_evento_w
	from	prescr_mat_alteracao
	where	nr_sequencia = nr_seq_evento_p;

end if;

return	dt_evento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
