CREATE OR REPLACE FUNCTION obter_data_fim_gaso (nr_prescricao_p bigint, nr_seq_gasoterapia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_fim_horario_w timestamp;


BEGIN

select	max(a.dt_fim_horario)
into STRICT	dt_fim_horario_w
from	prescr_gasoterapia_hor a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_seq_gasoterapia = nr_seq_gasoterapia_p
and	a.nr_sequencia =(	SELECT	max(b.nr_sequencia)
				from	prescr_gasoterapia_hor b
				where	b.nr_prescricao = a.nr_prescricao
				and	b.nr_sequencia = a.nr_sequencia
				and	coalesce(b.dt_suspensao::text, '') = ''
				and	(b.dt_lib_horario IS NOT NULL AND b.dt_lib_horario::text <> ''));
return dt_fim_horario_w;

end;
