CREATE OR REPLACE FUNCTION obter_data_fim_hist_unidade ( nr_seq_unidade_p bigint, nr_seq_historico_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_hist_w		bigint;
ds_retorno_w		timestamp;



BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_hist_w
from	unidade_atend_hist
where	nr_seq_unidade	= nr_seq_unidade_p;

if (nr_seq_hist_w = nr_seq_historico_p) then
	ds_retorno_w := clock_timestamp();
else
	select	coalesce(min(dt_historico),clock_timestamp())
	into STRICT	ds_retorno_w
	from	unidade_atend_hist
	where	nr_seq_unidade = nr_seq_unidade_p
	and	nr_sequencia > nr_seq_historico_p
	and	(dt_historico IS NOT NULL AND dt_historico::text <> '');
end if;

return	ds_retorno_w;

end;
