CREATE OR REPLACE FUNCTION obter_data_final_fanep_graf ( nr_seq_pepo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_termino_w		timestamp;
dt_termino_prevista_w	timestamp;
dt_retorno_w		timestamp;


BEGIN

if (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then
	select coalesce(dt_inicio_proced,dt_cirurgia) + coalesce((qt_minutos_prev_duracao / 1440),0),
	       dt_fim_cirurgia
	into STRICT   dt_termino_prevista_w,
	       dt_termino_w
	from   pepo_cirurgia
	where  nr_sequencia = nr_seq_pepo_p;

	if (coalesce(dt_termino_w::text, '') = '') and (dt_termino_prevista_w < clock_timestamp()) then
		dt_retorno_w	:= clock_timestamp() + (1/24);
	elsif (coalesce(dt_termino_w::text, '') = '') and (dt_termino_prevista_w >= clock_timestamp()) then
		dt_retorno_w	:= dt_termino_prevista_w;
	elsif (dt_termino_w IS NOT NULL AND dt_termino_w::text <> '') then
		dt_retorno_w	:= dt_termino_w;
	end if;

end if;

return	coalesce(dt_retorno_w, clock_timestamp()) + (10*60/86400);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
