SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_grafico_disp_html ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_seq_pepo_p bigint, nr_cirurgia_p bigint, ie_tipo_ativacao_p text, ie_opcao_p text, qt_dias_p bigint) RETURNS timestamp AS $body$
DECLARE

/*
ie_opcao_p

I - Inicio
F - Fim

*/
qt_registro_w	integer;
dt_retorno_w	timestamp;



BEGIN

if (ie_opcao_p = 'I') then
	if (ie_tipo_ativacao_p = 'A') then
		SELECT	coalesce(MIN(dt_instalacao),TRUNC(clock_timestamp(),'dd') - qt_dias_p)
		into STRICT	dt_retorno_w
		FROM	atend_pac_dispositivo a
		WHERE	nr_atendimento	=	nr_atendimento_p
		AND		TRUNC(dt_instalacao,'dd') >= (TRUNC(clock_timestamp(),'dd') - qt_dias_p);
	elsif (ie_tipo_ativacao_p = 'P') then
		SELECT	coalesce(MIN(dt_instalacao),TRUNC(clock_timestamp(),'dd') - qt_dias_p)
		into STRICT	dt_retorno_w
		FROM	atend_pac_dispositivo a
		WHERE	EXISTS (	SELECT 	1
							FROM 	atendimento_paciente b
							WHERE 	a.nr_atendimento 	= b.nr_atendimento
							AND    	b.cd_pessoa_fisica  = cd_pessoa_fisica_p)
		AND		TRUNC(dt_instalacao,'dd') >= (TRUNC(clock_timestamp(),'dd') - qt_dias_p);
	elsif (ie_tipo_ativacao_p = 'PC') then
		SELECT	coalesce(MIN(dt_instalacao),TRUNC(clock_timestamp(),'dd') - qt_dias_p)
		into STRICT	dt_retorno_w
		FROM	atend_pac_dispositivo a
		WHERE	nr_seq_pepo = nr_seq_pepo_p
		AND		TRUNC(dt_instalacao,'dd') >= (TRUNC(clock_timestamp(),'dd') - qt_dias_p);
	elsif (ie_tipo_ativacao_p = 'C') then
		SELECT	coalesce(MIN(dt_instalacao),TRUNC(clock_timestamp(),'dd') - qt_dias_p)
		into STRICT	dt_retorno_w
		FROM	atend_pac_dispositivo a
		WHERE	nr_cirurgia = nr_cirurgia_p
		AND		TRUNC(dt_instalacao,'dd') >= (TRUNC(clock_timestamp(),'dd') - qt_dias_p);
	end if;
else
	select	count(*)
	into STRICT	qt_registro_w
	from	atend_pac_dispositivo
	where	nr_atendimento	=	nr_atendimento_p
	and	coalesce(dt_retirada::text, '') = '';

	dt_retorno_w	:= clock_timestamp();
	if (qt_registro_w = 0) then
		select	coalesce(max(dt_retirada),clock_timestamp())
		into STRICT	dt_retorno_w
		from	atend_pac_dispositivo
		where	nr_atendimento	=	nr_atendimento_p;
	end if;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
