SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_grafico_disp ( nr_atendimento_p bigint, qt_dias_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

/*
ie_opcao_p

I - Inicio
F - Fim

*/
qt_registro_w	integer;
dt_retorno_w	timestamp;



BEGIN

if (ie_opcao_p = 'I') then
	select	coalesce(min(dt_instalacao),trunc(clock_timestamp(),'dd') - qt_dias_p)
	into STRICT	dt_retorno_w
	from	atend_pac_dispositivo
	where	nr_atendimento	=	nr_atendimento_p
	and	trunc(dt_instalacao,'dd') >= (trunc(clock_timestamp(),'dd') - qt_dias_p);
else
	select	count(*)
	into STRICT	qt_registro_w
	from	atend_pac_dispositivo
	where	nr_atendimento	=	nr_atendimento_p
	and	coalesce(dt_retirada::text, '') = '';

	dt_retorno_w	:= clock_timestamp();
	if (qt_registro_w = 0) then
		select	coalesce(max(dt_retirada),clock_timestamp())
		into STRICT	dt_retorno_w
		from	atend_pac_dispositivo
		where	nr_atendimento	=	nr_atendimento_p;
	end if;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
