CREATE OR REPLACE FUNCTION obter_data_grafico_pepo (ie_tipo_p text, nr_cirurgia_p bigint) RETURNS timestamp AS $body$
DECLARE

/*
   I  = Inicio procedimento
   f  = Fim procedimento
   II = Intervalo inicio
  IF = Intervalo fim
*/
dt_retorno_w    timestamp;

BEGIN

if (ie_tipo_p = 'I') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	max(dt_registro)
	into STRICT	dt_retorno_w
	from	evento_cirurgia_paciente a,
		evento_cirurgia b
	where	nr_cirurgia =  nr_cirurgia_p
	and	b.nr_sequencia = a.nr_seq_evento
	and 	ie_etapa_cirurgia = 3
	and	coalesce(a.ie_situacao,'A') = 'A';
end if;

if (ie_tipo_p = 'F') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	coalesce(max(dt_registro),clock_timestamp())
	into STRICT	dt_retorno_w
	from	evento_cirurgia_paciente a,
		evento_cirurgia b
	where	nr_cirurgia =  nr_cirurgia_p
	and	b.nr_sequencia = a.nr_seq_evento
	and 	ie_etapa_cirurgia = 4
	and	coalesce(a.ie_situacao,'A') = 'A';
end if;

if (ie_tipo_p = 'II') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	coalesce(max((dt_registro + 86401/86400) - 1),(clock_timestamp() + interval '86401 days'/86400) - 1)
	into STRICT	dt_retorno_w
	from	evento_cirurgia_paciente a,
		evento_cirurgia b
	where	nr_cirurgia =  nr_cirurgia_p
	and	b.nr_sequencia = a.nr_seq_evento
	and 	ie_etapa_cirurgia = 4
	and	coalesce(a.ie_situacao,'A') = 'A';
end if;

if (ie_tipo_p = 'IF') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select  max((dt_registro + 86399/86400) - 1)
	into STRICT	dt_retorno_w
	from	evento_cirurgia_paciente a,
		evento_cirurgia b
	where	nr_cirurgia =  nr_cirurgia_p
	and	b.nr_sequencia = a.nr_seq_evento
	and 	ie_etapa_cirurgia = 3
	and	coalesce(a.ie_situacao,'A') = 'A';
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
