SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_hem ( ie_tipo_data_p text, nr_seq_prod_reserva_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

/*
Tipos de data:
I - In√≠cio
F - Final
*/
if (upper(ie_tipo_data_p)	= 'I') then
	select	max(dt_alteracao)
	into STRICT	dt_retorno_w
	from	prescr_solucao_evento
	where	nr_seq_prod_reserva = nr_seq_prod_reserva_p
	and	ie_alteracao = 1
	and	ie_evento_valido = 'S';
elsif (upper(ie_tipo_data_p)	= 'F') then
	select	max(dt_alteracao)
	into STRICT	dt_retorno_w
	from	prescr_solucao_evento
	where	nr_seq_prod_reserva = nr_seq_prod_reserva_p
	and	ie_alteracao = 4
	and	ie_evento_valido = 'S';
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
