CREATE OR REPLACE FUNCTION obter_data_inicio_cirurgia ( nr_cirurgia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_evento_w timestamp;


BEGIN

select	max(a.dt_registro)
into STRICT	dt_evento_w
from	evento_cirurgia b,
	evento_cirurgia_paciente a
where 	a.nr_seq_evento = b.nr_sequencia
and	b.ie_inicia_cirurgia = 'S'
and 	nr_cirurgia = nr_cirurgia_p
and	coalesce(a.ie_situacao,'A') = 'A';


return	dt_evento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
