CREATE OR REPLACE FUNCTION obter_data_ini_trat (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


dt_retorno_w	varchar(255);


BEGIN

SELECT 	to_char(MIN(dt_inicio), 'yyyy-mm-dd') --Fixo para o MÃ©xico
into STRICT 	dt_retorno_w
FROM 	DIAGNOSTICO_DOENCA DD,
		cid_doenca CD,
		atendimento_paciente AP
WHERE 	DD.nr_atendimento = AP.nr_atendimento
AND 	AP.cd_pessoa_fisica =  cd_pessoa_fisica_p
AND 	UPPER(trim(both DD.cd_doenca)) = UPPER(trim(both CD.cd_doenca_cid))
AND 	CD.ie_doenca_cronica_mx IN ('DIABT', 'OBSDA', 'HIPAS', 'LIPDM', 'MTABO')
AND 	(DD.dt_inicio IS NOT NULL AND DD.dt_inicio::text <> '')
AND 	(DD.dt_liberacao IS NOT NULL AND DD.dt_liberacao::text <> '') AND coalesce(DD.dt_inativacao::text, '') = '';

return dt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
