CREATE OR REPLACE FUNCTION obter_data_internacao (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_entrada_w	varchar(100);


BEGIN

select	to_char(min(a.dt_entrada_unidade),'dd/mm/yyyy hh24:mi:ss')
into STRICT	dt_entrada_w
from	setor_atendimento b,
	atend_paciente_unidade a
where	a.nr_atendimento		= nr_atendimento_p
and	a.cd_setor_atendimento	= b.cd_setor_atendimento
and	b.cd_classif_setor		in ('3','4');

return	dt_entrada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
