SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_item_lote ( nr_seq_lote_p bigint, ds_mascara_p text) RETURNS varchar AS $body$
DECLARE


dt_horario_w			varchar(20);
ds_mascara_w			varchar(20);

BEGIN

if (ds_mascara_p = '') then
	ds_mascara_w := 'dd/mm/yyyy hh24:mi:ss';
else
	ds_mascara_w := ds_mascara_p;
end if;

select	min(to_char(dt_horario, ds_mascara_w)) dt_horario
into STRICT	dt_horario_w
from	prescr_mat_hor
where	nr_seq_lote = nr_seq_lote_p
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

return	dt_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
