SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_leite_deriv ( nr_prescricao_p bigint, ie_tipo_data_p text default 'm') RETURNS timestamp AS $body$
DECLARE


dt_menor_w	timestamp := to_date('30/12/1899 00:00:00','dd/mm/yyyy hh24:mi:ss');
dt_maior_w	timestamp := to_date('30/12/1899 00:00:00','dd/mm/yyyy hh24:mi:ss');

BEGIN

select	coalesce(max(dt_horario),dt_maior_w),
	coalesce(min(dt_horario),dt_menor_w)
into STRICT	dt_maior_w,
	dt_menor_w
from	prescr_mat_hor a,
	prescr_medica b
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_prescricao = b.nr_prescricao
and	a.ie_agrupador = 16
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(a.dt_suspensao::text, '') = ''
and	coalesce(b.dt_suspensao::text, '') = '';

if (ie_tipo_data_p = 'M') then
	return	dt_maior_w;
else
	return	dt_menor_w;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
