SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_lib_proced (cd_agenda_p bigint, cd_profissional_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_pessoa_fisica_p text, nr_seq_proc_interno_p bigint, nr_seq_agenda_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_tempo_w		bigint;
qt_minuto_adic_w		bigint;
qt_anos_w		integer;
nr_seq_agenda_w		agenda_paciente.nr_sequencia%type;
dt_liberada_w		timestamp;

dt_agenda_w		timestamp;
ie_dia_semana_w		smallint;


BEGIN
dt_liberada_w := (clock_timestamp() - interval '9999 days');
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select	max(hr_inicio)
	into STRICT	dt_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;
	
	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
		ie_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_w);
	else
		ie_dia_semana_w	:= null;
	end if;
	
end if;

if	(cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '' AND ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') or (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	
	begin
	select	(obter_idade(dt_nascimento, clock_timestamp(), 'A'))::numeric
	into STRICT	qt_anos_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	exception
	when others then
		qt_anos_w := 0;
	end;
	
	
	if (nr_seq_proc_interno_p > 0) then
	
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_tempo_w
		from	tempo_procedimento
		where	nr_seq_proc_interno = nr_seq_proc_interno_p
		and	cd_agenda = cd_agenda_p
		and	coalesce(cd_pessoa_fisica, coalesce(cd_profissional_p,'0'))	= coalesce(cd_profissional_p,'0')
		and	qt_anos_w  between coalesce(qt_idade_minima,0) and coalesce(qt_idade_maxima,999)
		and	coalesce(qt_minuto,0) > 0
		and	((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_semana_w not in (1,7))) or (coalesce(ie_dia_semana::text, '') = ''));
		
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_agenda_w
		from	agenda_paciente
		where	nr_seq_proc_interno = nr_seq_proc_interno_p
		and	cd_agenda = cd_agenda_p
		and	nr_sequencia <> nr_seq_agenda_p;
		
	elsif (cd_procedimento_p > 0) and (ie_origem_proced_p > 0) then
		
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_tempo_w
		from	tempo_procedimento
		where	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p
		and	cd_agenda	= cd_agenda_p
		and	coalesce(cd_pessoa_fisica, coalesce(cd_profissional_p,'0'))	= coalesce(cd_profissional_p,'0')
		and	qt_anos_w  between coalesce(qt_idade_minima,0) and coalesce(qt_idade_maxima,999)
		and	coalesce(qt_minuto,0) > 0
		and	((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_semana_w not in (1,7))) or (coalesce(ie_dia_semana::text, '') = ''));
		
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_agenda_w
		from	agenda_paciente
		where	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p
		and	cd_agenda = cd_agenda_p
		and	nr_sequencia <> nr_seq_agenda_p;
	end if;

	if (nr_seq_tempo_w > 0) and (nr_seq_agenda_w > 0) then
	
		select	coalesce(qt_minuto_adic,0)
		into STRICT	qt_minuto_adic_w
		from	tempo_procedimento
		where	nr_sequencia = nr_seq_tempo_w;
		
		IF (qt_minuto_adic_w > 0) THEN
			select	hr_inicio + ((nr_minuto_duracao + qt_minuto_adic_w)/1440)			
			into STRICT	dt_liberada_w
			from	agenda_paciente
			where	nr_sequencia = nr_seq_agenda_w;
		END IF;
	end if;
end if;

return dt_liberada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
