CREATE OR REPLACE FUNCTION obter_data_limite_autor (nr_seq_autorizacao_p bigint) RETURNS timestamp AS $body$
DECLARE


ie_tipo_autorizacao_w	varchar(2);
cd_convenio_w		integer;
nr_seq_agenda_w		autorizacao_convenio.nr_seq_agenda%type;
dt_agenda_w		timestamp;
dt_retorno_w		timestamp;
cd_agenda_w		bigint;


BEGIN

dt_retorno_w	:= null;

select 	max(cd_convenio),
	max(ie_tipo_autorizacao),
	max(nr_seq_agenda)
into STRICT	cd_convenio_w,
	ie_tipo_autorizacao_w,
	nr_seq_agenda_w
from 	autorizacao_convenio
where	nr_sequencia	= nr_seq_autorizacao_p;

if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
	select 	max(dt_agenda),
		max(cd_agenda)
	into STRICT	dt_agenda_w,
		cd_agenda_w
	from 	agenda_paciente
	where	nr_sequencia	= nr_seq_agenda_w;

	if (ie_tipo_autorizacao_w = '3') then
		dt_retorno_w := obter_data_autor_convenio(cd_convenio_w,dt_agenda_w,'S','C','P',cd_agenda_w);
	elsif (ie_tipo_autorizacao_w = '4') then
		dt_retorno_w := obter_data_autor_convenio(cd_convenio_w,dt_agenda_w,'S','C','M',cd_agenda_w);
	end if;
	
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
