CREATE OR REPLACE FUNCTION obter_data_lote ( nr_seq_lote_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

dt_atend_lote_w			timestamp;
dt_disp_farmacia_w		timestamp;
dt_entrega_setor_w		timestamp;
dt_recebimento_setor_w	timestamp;
dt_retorno_w			timestamp;

BEGIN
select	dt_atend_lote,
		dt_disp_farmacia,
		dt_entrega_setor,
		dt_recebimento_setor
into STRICT		dt_atend_lote_w,
		dt_disp_farmacia_w,
		dt_entrega_setor_w,
		dt_recebimento_setor_w
from		ap_lote
where	nr_sequencia = nr_seq_lote_p;

if (ie_opcao_p = 'A') then
	dt_retorno_w := dt_atend_lote_w;
elsif (ie_opcao_p = 'D') then
	dt_retorno_w := dt_disp_farmacia_w;
elsif (ie_opcao_p = 'E') then
	dt_retorno_w := dt_entrega_setor_w;
else
	dt_retorno_w := dt_recebimento_setor_w;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
