SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_marca_material ( ds_marca_p text, cd_material_p bigint) RETURNS timestamp AS $body$
DECLARE



nr_seq_marca_w		bigint;
dt_atualizacao_w		timestamp;



BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_marca_w
from	marca b,
	material_marca a
where	a.cd_material		= cd_material_p
and	a.nr_sequencia		= b.nr_sequencia
and	((upper(substr(CASE WHEN a.cd_referencia='' THEN b.ds_marca  ELSE b.ds_marca || '(' || a.cd_referencia || ')' END ,1,255)) = upper(ds_marca_p)) or (upper(b.ds_marca) = upper(ds_marca_p)));

if (nr_seq_marca_w > 0) then

	select	max(dt_atualizacao)
	into STRICT	dt_atualizacao_w
	from	material_marca
	where	nr_sequencia	= nr_seq_marca_w
	and	cd_material	= cd_material_p;

end if;

return	dt_atualizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
