SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_matpaci (nr_seq_material_p text, ie_tipo_data_p text) RETURNS timestamp AS $body$
DECLARE


dt_atendimento_w  		timestamp;
dt_conta_w   		timestamp;
dt_conferencia_w		timestamp;


BEGIN

SELECT dt_atendimento,
	dt_conta,
	dt_conferencia
INTO STRICT dt_atendimento_w,
	dt_conta_w,
	dt_conferencia_w
FROM material_atend_paciente
WHERE nr_sequencia = nr_seq_material_p;

IF (ie_tipo_data_p = 'R') then
	return	dt_conferencia_w;
end if;
IF (ie_tipo_data_p = 'A') then
	return	 dt_atendimento_w;
ELSE
	return	 dt_conta_w;
END IF;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
