CREATE OR REPLACE FUNCTION obter_data_medic_dose_dif (nr_prescricao_p bigint, hr_prim_horario_p text) RETURNS timestamp AS $body$
DECLARE


dt_w	varchar(10);
hr_w	varchar(8);
dt_ww	timestamp;



BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	to_char(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_inicio_prescr,dt_prescricao)),'dd/mm/yyyy') td,
		to_char(dt_primeiro_horario,'hh24:mi:ss') hr
	into STRICT	dt_w,
		hr_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	if (hr_prim_horario_p IS NOT NULL AND hr_prim_horario_p::text <> '') and (hr_prim_horario_p <> '  :  ') then

		dt_ww	:= to_date(dt_w || ' ' || hr_prim_horario_p || ':00','dd/mm/yyyy hh24:mi:ss');

	else

		dt_ww	:= to_date(dt_w || ' ' || hr_w,'dd/mm/yyyy hh24:mi:ss');

	end if;

end if;

return dt_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
