CREATE OR REPLACE FUNCTION obter_data_min_resgate (nr_seq_aplicacao_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_min_resg_w		timestamp;
nr_dias_min_resg_w	bigint;
dt_aplicacao_w		timestamp;


BEGIN

select	max(a.dt_aplicacao),
	coalesce(max(b.nr_dias_min_resg),0)
into STRICT	dt_aplicacao_w,
	nr_dias_min_resg_w
from	banco_aplicacao a,
	conta_banco_tipo_aplic b
where	a.nr_seq_aplicacao = b.nr_sequencia
and	a.nr_sequencia = nr_seq_aplicacao_p;

dt_min_resg_w := dt_aplicacao_w;
if (nr_dias_min_resg_w > 0) then
	dt_min_resg_w := dt_aplicacao_w + nr_dias_min_resg_w;
end if;

return dt_min_resg_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
