CREATE OR REPLACE FUNCTION obter_data_nota_fiscal ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_entrada_saida_w		timestamp;
dt_emissao_w			timestamp;
dt_atualizacao_estoque_w		timestamp;
dt_retorno_w			timestamp;

/* 	ES - data entrada/sa√≠da
	EM - data de emissao
	AE - Data atualizacao estoque
*/
BEGIN

select	dt_entrada_saida,
	dt_emissao,
	dt_atualizacao_estoque
into STRICT	dt_entrada_saida_w,
	dt_emissao_w,
	dt_atualizacao_estoque_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'ES') then
	dt_retorno_w := dt_entrada_saida_w;
elsif (ie_opcao_p = 'EM') then
	dt_retorno_w := dt_emissao_w;
elsif (ie_opcao_p = 'AE') then
	dt_retorno_w := dt_atualizacao_estoque_w;
end if;


return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
