SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_ofic_funcao (cd_cnpj_cliente_p com_cliente.cd_cnpj%type, nr_seq_cliente_p com_cliente.nr_sequencia%type, cd_funcao_p funcao.cd_funcao%type) RETURNS timestamp AS $body$
DECLARE

dt_oficializacao_w timestamp;

BEGIN

	select max(dt)
	into STRICT dt_oficializacao_w
	from (
	  SELECT max(c.dt_oficializacao)as dt
	  from com_cli_ofic_uso_mod_func a
		  inner join com_cli_ofic_uso_mod b on a.nr_seq_mod_ofic_uso = b.nr_sequencia
		  inner join com_cli_ofic_uso c on c.nr_seq_cliente = nr_seq_cliente_p
				and b.nr_seq_ofic = c.nr_sequencia
	  where a.cd_funcao = cd_funcao_p
	
union

	  SELECT max(d.dt_oficializacao)as dt
	  from funcao a
		inner join modulo_implantacao b on a.nr_seq_mod_impl = b.nr_sequencia
		inner join com_cli_ofic_uso_mod c on c.nr_seq_mod_impl = b.nr_sequencia
		inner join com_cli_ofic_uso d on d.nr_sequencia = c.nr_seq_ofic
			  and d.nr_seq_cliente = nr_seq_cliente_p
	  where a.cd_funcao = cd_funcao_p
	) alias3;

	-- caso nao tenha achado nenhuma ufo do cliente verifica os outros cnpjs iguais ao dele 
	if coalesce(dt_oficializacao_w::text, '') = '' then

		select max(dt)
		into STRICT dt_oficializacao_w
		from (
		  SELECT max(c.dt_oficializacao)as dt
		  from com_cli_ofic_uso_mod_func a
			  inner join com_cli_ofic_uso_mod b on a.nr_seq_mod_ofic_uso = b.nr_sequencia
			  inner join com_cli_ofic_uso c on c.nr_seq_cliente in (
				SELECT nr_sequencia from com_cliente where cd_cnpj = cd_cnpj_cliente_p
			)
				and b.nr_seq_ofic = c.nr_sequencia
		  where a.cd_funcao = cd_funcao_p
		
union

		  select max(d.dt_oficializacao)as dt
		  from funcao a
			inner join modulo_implantacao b on a.nr_seq_mod_impl = b.nr_sequencia
			inner join com_cli_ofic_uso_mod c on c.nr_seq_mod_impl = b.nr_sequencia
			inner join com_cli_ofic_uso d on d.nr_sequencia = c.nr_seq_ofic
			  and d.nr_seq_cliente in(
				select nr_sequencia from com_cliente where cd_cnpj = cd_cnpj_cliente_p
			  )
		  where a.cd_funcao = cd_funcao_p
		) alias6;

	end if;
	return dt_oficializacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
