CREATE OR REPLACE FUNCTION obter_data_ordem_compra ( nr_ordem_compra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
dt_ordem_compra_w		timestamp;
dt_inclusao_w			timestamp;
dt_emissao_w			timestamp;
dt_entrega_w			timestamp;
dt_aprovacao_w		timestamp;
dt_baixa_w			timestamp;
dt_liberacao_w		timestamp;

/* 	O = data ordem compra 
	I = data inclusao 
	M = data emissao 
	E = data entrega 
	A = data aprovacao 
	B = data baixa 
	L = data liberacao 
*/
 
 

BEGIN 
 
select	dt_ordem_compra, 
	dt_inclusao, 
	dt_emissao, 
	dt_entrega, 
	dt_aprovacao, 
	dt_baixa, 
	dt_liberacao 
into STRICT	dt_ordem_compra_w, 
	dt_inclusao_w, 
	dt_emissao_w, 
	dt_entrega_w, 
	dt_aprovacao_w, 
	dt_baixa_w, 
	dt_liberacao_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (ie_opcao_p = 'O') then 
	return dt_ordem_compra_w;
elsif (ie_opcao_p = 'I') then 
	return dt_inclusao_w;
elsif (ie_opcao_p = 'M') then 
	return dt_emissao_w;
elsif (ie_opcao_p = 'E') then 
	return dt_entrega_w;
elsif (ie_opcao_p = 'A') then 
	return dt_aprovacao_w;
elsif (ie_opcao_p = 'B') then 
	return dt_baixa_w;
elsif (ie_opcao_p = 'L') then 
	return dt_liberacao_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
