SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_passagem_atend ( nr_atendimento_p bigint, ie_tipo_gerar_p text) RETURNS timestamp AS $body$
DECLARE

 
dt_retorno_w	timestamp;
qt_registro_w	bigint;		
			 

BEGIN 
 
if (ie_tipo_gerar_p = 'S') then 
	dt_retorno_w := clock_timestamp();
else 
	select	max(dt_entrada) 
	into STRICT	dt_retorno_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	qt_registro_w := 1;
	 
	while(qt_registro_w > 0) loop 
	 
		dt_retorno_w := dt_retorno_w + 1/86400;
		 
		select	count(*) 
		into STRICT	qt_registro_w 
		from	atend_paciente_unidade 
		where	nr_atendimento = nr_atendimento_p 
		and	dt_entrada_unidade = dt_retorno_w;
		 
	end loop;
	 
end if;
 
return	dt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
