SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_penult_login_valido ( nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE


dt_aux_w	timestamp;
dt_login_w	timestamp;

c01 CURSOR FOR
SELECT	dt_acesso from (
select	dt_acesso
from	tasy_log_acesso
where	upper(nm_usuario) = upper(nm_usuario_p)
and	ie_result_acesso = 'N'
order by
	dt_acesso desc
) alias2 LIMIT 2;


BEGIN
	dt_login_w := clock_timestamp();
	open c01;
	loop
	fetch c01 into dt_aux_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (dt_aux_w IS NOT NULL AND dt_aux_w::text <> '') then
				dt_login_w := dt_aux_w;
			end if;
		end;
	end loop;
	close c01;

	return dt_login_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
