CREATE OR REPLACE FUNCTION obter_data_prep_reaprov (nr_seq_etiq_reapro_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w    timestamp;


BEGIN
if (nr_seq_etiq_reapro_p IS NOT NULL AND nr_seq_etiq_reapro_p::text <> '') then

    select  max(coalesce(a.dt_preparo,b.dt_preparo))
    into STRICT    dt_retorno_w
    from    adep_processo_frac a,
			adep_processo b
    where   a.nr_sequencia = nr_seq_etiq_reapro_p
	and		a.nr_seq_processo = b.nr_sequencia;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
