CREATE OR REPLACE FUNCTION obter_data_prev_alta_atend (nr_atendimento_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

 
dt_retorno_w		timestamp;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	if (ie_opcao_p = 'DAP') then 
 
		SELECT	dt_previsto_alta 
		INTO STRICT	dt_retorno_w 
		FROM	atendimento_paciente a 
		WHERE	a.nr_atendimento = nr_atendimento_p;
 
	end if;
end if;
 
return	dt_retorno_w;
 
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
