CREATE OR REPLACE FUNCTION obter_data_prev_futura ( dt_inicial timestamp, qt_dias bigint, ie_converte_mes text) RETURNS timestamp AS $body$
DECLARE


mes_w bigint;

BEGIN

mes_w := qt_dias/30;

if (ie_converte_mes = 'S') then
	return pkg_date_utils.ADD_MONTH(dt_inicial ,mes_w);
else
	return dt_inicial + qt_dias;
end if;

end;
$body$
LANGUAGE PLPGSQL
