SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_prev_grupo (nr_atendimento_p bigint, nr_grupo_p bigint ) RETURNS timestamp AS $body$
DECLARE


dt_entrada_w		timestamp;
dt_previsto_perma_w	timestamp;
qt_dia_previsao_w	bigint;


BEGIN


Select 	max(dt_entrada)
into STRICT	dt_entrada_w
from	pac_grupo_atend a,
	PAC_GRUPO d
where 	a.nr_seq_grupo = d.nr_sequencia
and	a.nr_atendimento = nr_atendimento_p
and	d.nr_sequencia = nr_grupo_p;

Select 	coalesce(max(qt_dia_previsao),0)
into STRICT	qt_dia_previsao_w
from	pac_grupo
where	nr_sequencia = nr_grupo_p;

if (qt_dia_previsao_w > 0) then

	dt_previsto_perma_w := dt_entrada_w + qt_dia_previsao_w;

end if;

return	dt_previsto_perma_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
