CREATE OR REPLACE FUNCTION obter_data_prev_term_etapa_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_tipo_solucao_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_evento_w		bigint;
dt_prev_termino_w	timestamp;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (ie_tipo_solucao_p IS NOT NULL AND ie_tipo_solucao_p::text <> '') then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_evento_w
	from	prescr_solucao_evento
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p
	and	ie_tipo_solucao	= 1
	and	(dt_prev_termino_etapa IS NOT NULL AND dt_prev_termino_etapa::text <> '')
	and	ie_evento_valido	= 'S';

	if (nr_seq_evento_w > 0) then

		select	max(dt_prev_termino_etapa)
		into STRICT	dt_prev_termino_w
		from	prescr_solucao_evento
		where	nr_sequencia = nr_seq_evento_w;

	else
		dt_prev_termino_w := null;

	end if;

end if;

return dt_prev_termino_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
