CREATE OR REPLACE FUNCTION obter_data_pre_venda ( nr_pre_venda_p bigint, ie_tipo_dado_p text) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;

BEGIN
if (nr_pre_venda_p IS NOT NULL AND nr_pre_venda_p::text <> '') and (ie_tipo_dado_p IS NOT NULL AND ie_tipo_dado_p::text <> '') then
	begin
	if (ie_tipo_dado_p = 'DOV') then
		begin
		select	dt_ordem_venda
		into STRICT	dt_retorno_w
		from	pre_venda
		where	nr_sequencia = nr_pre_venda_p;
		end;
	end if;
	end;
end if;

return dt_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
